<?php $this->lang->load('comments/comments'); ?><h3><?php echo lang('comments.title');?></h3>		<fieldset class="alternative float-left width-half">	<legend><?php echo lang('comments.user_comments');?></legend>		<?php $comments = $this->comments_m->get_comments(array('module' => $module, 'module_id' => $module_id, 'is_active' => 1));	if(!empty($comments)): ?>		{literal}	<ul class="comments_list">	<?php foreach($comments as $comment): ?>		<!-- Comment -->		<li class="comment">			<!-- Comment heading -->			<div class="comment_heading">				<?php echo gravatar($comment->email,40); ?>				<strong class="comment_author"><?php echo $comment->name; ?></strong>				<p class="comment_date"><?php echo date('M d, Y', $comment->created_on); ?></p>			</div>			<!-- Comment body -->			<div class="comment_body">				<p><?php echo nl2br(stripslashes($comment->body));?></p>			</div>		</li>	<?php endforeach; ?>	</ul>	{/literal}	<?php else: ?>	<p><?php echo lang('comments.no_comments');?></p><?php endif; ?>	</fieldset>								<fieldset class="float-right width-half">	<legend><?php echo lang('comments.your_comment');?></legend>	<?php echo $this->load->view('comments/form', array('module'=>$module, 'id' => $module_id)); ?> </fieldset>